mapper.select:
SELECT *
FROM genFile
WHERE 1

	AND relTypeCode = #{relTypeCode}


	AND relId = #{relId}


	AND typeCode = #{typeCode}


	AND type2Code = #{type2Code}


	AND fileNo = #{fileNo}

SELECT *
FROM genFile
WHERE 1
AND relTypeCode = #{relTypeCode}
AND relId =
#{relId}

	AND typeCode = #{typeCode}


	AND type2Code = #{type2Code}

ORDER BY typeCode, type2Code, fileNo

SELECT *
FROM genFile
WHERE 1
AND relTypeCode = #{relTypeCode}
AND relId = #{relId}
ORDER BY typeCode, type2Code, fileNo

SELECT *
FROM genFile
WHERE 1

	AND relId IN

		#{relId}


AND relTypeCode = #{relTypeCode}
AND typeCode = #{typeCode}
AND
type2Code = #{type2Code}
ORDER BY relId, fileNo

mapper.update:
UPDATE genFile

	updateDate = NOW(),
	relId = #{relId}

WHERE id = #{id}

mapper.delete:
DELETE FROM genFile
WHERE relTypeCode = #{relTypeCode}
AND relId = #{relId}
